export class LottieConfigCreator {
    createWithSrc(container, src, autoplay, loop) {
        return {
            container,
            renderer: 'svg',
            loop,
            autoplay,
            path: src,
        };
    }
    createWithData(container, animationData, autoplay, loop) {
        return {
            container,
            renderer: 'svg',
            loop,
            autoplay,
            animationData,
        };
    }
}
